import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { playSong, toggleLike, getSongsAction } from '../redux/actions/Actions';
import SongCard from './SongCard';
import { Form, Button } from 'react-bootstrap';

const SongList = () => {
  const dispatch = useDispatch();
  const songs = useSelector(state => state.list) || [];
  const likedSongs = useSelector(state => state.likedSongs) || [];
  const error = useSelector(state => state.error);
  const [query, setQuery] = useState('');

  useEffect(() => {
    dispatch(getSongsAction('sabrina carpenter'));
  }, [dispatch]);

  const handlePlay = (song) => {
    dispatch(playSong(song));
  };

  const handleLike = (songId) => {
    dispatch(toggleLike(songId));
  };

  const handleSearch = (e) => {
    e.preventDefault(); // Previene il comportamento predefinito del form
    if (query.trim()) {
      dispatch(getSongsAction(query));
    }
  };

  return (
    <div>
      <Form className="search" onSubmit={handleSearch}>
        <Form.Group controlId="searchForm">
          <Form.Control
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Cerca"
          />
          <Button variant="primary" type="submit">GO</Button>
        </Form.Group>
      </Form>
      {error && <p>Error: {error}</p>}
      <div className="song-list">
        {songs.map(song => (
          <SongCard
            key={song.id}
            song={song}
            onPlay={handlePlay}
            onLike={handleLike}
            isLiked={likedSongs.includes(song.id)}
          />
        ))}
      </div>
    </div>
  );
};

export default SongList;
