// src/components/SongList.js

import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { playSong, toggleLike, setError } from '../redux/actions/Actions';
import SongCard from './SongCard';
import { Form, Button } from 'react-bootstrap';
import { getSongsAction } from '../redux/actions/Actions';

const SongList = () => {
  const dispatch = useDispatch();
  const songs = useSelector(state => state.list) || [];
  const likedSongs = useSelector(state => state.likedSongs) || [];
  const error = useSelector(state => state.error);

  const [query, setQuery] = useState('');

  useEffect(() => {
    // Esegui una ricerca predefinita o non eseguirne nessuna
    // dispatch(getSongsAction('sabrina carpenter')); // Commenta o rimuovi se non desiderato
  }, [dispatch]);

  const handlePlay = (song) => {
    dispatch(playSong(song));
  };

  const handleLike = (songId) => {
    dispatch(toggleLike(songId));
  };

  const handleSearch = () => {
    if (query.trim()) {
      dispatch(getSongsAction(query));
    }
  };

  return (
    <div>
      <Form className="search">
        <Form.Group controlId="searchForm">
          <Form.Control
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Cerca"
          />
          <Button variant="primary" type="button" onClick={handleSearch}>GO</Button>
        </Form.Group>
      </Form> 
      {error && <p>Error: {error}</p>}
      <div className="song-list">
        {songs.length > 0 ? (
          songs.map(song => (
            <SongCard
              key={song.id}
              song={song}
              onPlay={handlePlay}
              onLike={handleLike}
              isLiked={likedSongs.includes(song.id)}
            />
          ))
        ) : (
          <p>No songs found. Please try a different search.</p>
        )}
      </div>
    </div>
  );
};

export default SongList;
