import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import 'bootstrap/dist/css/bootstrap.min.css';
import ColorSchemesExample from './components/NavBar';
import MainContent from './components/MainSection';
import SongList from './components/SongsList';
import { getSongsAction, playSong, toggleLike } from './redux/actions/Actions';
function App() {
  const dispatch = useDispatch();
  const songs = useSelector(state => state.list);
  const likedSongs = useSelector(state => state.likedSongs);

  const [query, setQuery] = useState('');

  useEffect(() => {
    searchSongs('sabrina carpenter'); 
  }, [dispatch]);

  const searchSongs = (query) => {
    dispatch(getSongsAction(query));
  };

  const handlePlay = (song) => {
    dispatch(playSong(song));
  };

  const handleLike = (songId) => {
    dispatch(toggleLike(songId));
  };

  const handleSearch = (newQuery) => {
    setQuery(newQuery);
    searchSongs(newQuery);
  };

  return (
    <div className="App">
      <ColorSchemesExample onSearch={handleSearch} /> 
      <MainContent />
      <SongList songs={songs} likedSongs={likedSongs} handlePlay={handlePlay} handleLike={handleLike} />
    </div>
  );
}

export default App;
