export const PLAY_SONG = 'PLAY_SONG';
export const TOGGLE_LIKE = 'TOGGLE_LIKE';
export const SET_SONGS = 'SET_SONGS';
export const SET_SEARCH_QUERY='SET_SEARCH_QUERY'
export const GET_SONGS = 'GET_SONGS';
export const SET_ERROR = 'SET_ERROR';
export const playSong = (song) => ({
  type: PLAY_SONG,
  payload: song,
});

export const toggleLike = (songId) => ({
  type: TOGGLE_LIKE,
  payload: songId,
});

export const setSongs = (songs) => ({
  type: SET_SONGS,
  payload: songs,
});

export const setError = (error) => ({
  type: SET_ERROR,
  payload: error,
});
export const setSearchQuery = (query) => ({
    type: 'SET_SEARCH_QUERY',
    payload: query,
  });

  export const getSongsAction = (query) => {
    return (dispatch) => {
      console.log('Initiating song search for query:', query);

      fetch(`https://striveschool-api.herokuapp.com/api/deezer/search?q=${encodeURIComponent(query)}`)
        .then((response) => {
          if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
          }
          return response.json();
        })
        .then((data) => {
          console.log('Received data:', data);
          if (!data.data || !Array.isArray(data.data)) {
            throw new Error('Unexpected data format received');
          }
          dispatch({
            type: GET_SONGS,
            payload: data.data,
          });
        })
        .catch((error) => {
          console.error('Error in getSongsAction:', error);
          dispatch({
            type: SET_ERROR,
            payload: error.message,
          });
        });
    };
  };
